import React, { useState, useEffect } from "react";
import { Card, Button, Alert } from "react-bootstrap";
import { useAuth } from "../contexts/AuthContext";
import Link from "next/link";
import { logout } from "../scripts/auth";
import {
  getUser,
  User,
  addListener,
  startDrawing,
  resetDrawing,
} from "../scripts/db";
import CircularProgress from "@material-ui/core/CircularProgress";
import { useRouter } from "next/router";
import { useAsync } from "../components/Utils";
import firebase from "firebase";

export default function Dashboard() {
  const [error, setError] = useState("");
  const [userData, setUserData] = useState<User>({
    name: "",
    desire: "",
    email: "",
    dislike: "",
    personDesire: "",
    personDislike: "",
  });
  const [command, setCommand] = useState<any>("not started");
  const { currentUser } = useAuth();
  const router = useRouter();

  // useEffect(() => {
  //   if (currentUser === null) {
  //     router.push("/login");
  //   }
  // });

  async function handleLogout() {
    setError("");

    try {
      await logout();
      router.push("/login");
    } catch {
      setError("Failed to log out");
    }
  }
  useAsync(
    () => getUser(currentUser.uid),
    (user: firebase.firestore.DocumentSnapshot) =>
      setUserData(user.data() as User)
  );
  // useEffect(() => {

  //   let isMounted = true;
  //   if (currentUser) {
  //     getUser(currentUser.uid).then((user) => {
  //       if (!isMounted) {
  //         return;
  //       }
  //       setUserData(user.data() as User);
  //     });
  //   }
  //   return () => (isMounted = false);
  // }, [currentUser, userData]);

  useEffect(() => {
    let unsubscribe = addListener("commands/commandCenter", (snap) => {
      setCommand(snap.data()?.state);
    });
    return unsubscribe;
  }, [command]);

  if (command === "not started") {
    return (
      <>
        <Card>
          <Card.Body>
            <>
              <h2 className="text-center mb-4">Profile</h2>
              {error && <Alert variant="danger">{error}</Alert>}
              <strong>Name:</strong> {userData.name}
              <br></br>
              <strong>Email:</strong> {userData.email}
              <br></br>
              <strong>Desires:</strong> {userData.desire}
              <br></br>
              <strong>Dislikes:</strong> {userData.dislike}
              {userData.userType === "admin" && (
                <>
                  <br></br>
                  <div className="w-100 text-center mt-2">
                    <Button
                      className="w-100"
                      onClick={startDrawing}
                      variant="primary"
                    >
                      Start Drawing
                    </Button>
                  </div>
                </>
              )}
              <Link href="/update-profile">
                <Button className="btn btn-primary w-100 mt-3">
                  Update Profile
                </Button>
              </Link>
            </>
          </Card.Body>
        </Card>
        <div className="w-100 text-center mt-2">
          <Button variant="link" onClick={handleLogout}>
            Log Out
          </Button>
        </div>
      </>
    );
  } else if (command === "started") {
    return (
      <>
        <Card>
          <Card.Body>
            <div className="text-center mt-2">
              <CircularProgress color="secondary" />
            </div>
            <div className="w-100 text-center mt-2">
              <h2>Pairing...</h2>
              {userData.userType === "admin" && (
                <>
                  <br></br>
                  <Button
                    onClick={resetDrawing}
                    variant="danger"
                    className="w-100"
                  >
                    Cancel Drawing
                  </Button>
                </>
              )}
            </div>
          </Card.Body>
        </Card>
      </>
    );
  } else {
    return (
      <>
        <Card>
          <Card.Body>
            <h2 className="text-center mb-4">Profile</h2>
            {error && <Alert variant="danger">{error}</Alert>}
            <strong>Name:</strong> {userData.name}
            <br></br>
            <strong>Email:</strong> {userData.email}
            <br></br>
            <strong>Desires:</strong> {userData.desire}
            <br></br>
            <strong>Dislikes:</strong> {userData.dislike}
            <br></br>
            <strong>Person:</strong> {userData.person}
            <br></br>
            <strong>Person Desire:</strong> {userData.personDesire}
            <br></br>
            <strong>Person Dislike:</strong> {userData.personDislike}
            {userData.userType === "admin" && (
              <>
                <br></br>
                <div className="w-100 text-center mt-2">
                  <Button
                    onClick={resetDrawing}
                    variant="danger"
                    className="w-100"
                  >
                    Reset Drawing
                  </Button>
                </div>
              </>
            )}
            <Link href="/update-profile">
              <Button className="btn btn-primary w-100 mt-3">
                Update Profile
              </Button>
            </Link>
          </Card.Body>
        </Card>
        <div className="w-100 text-center mt-2">
          <Button variant="link" onClick={handleLogout}>
            Log Out
          </Button>
        </div>
      </>
    );
  }
}
