import { db } from "../firebase";
export function addUser(id, info) {
  return db.collection("users").doc(id).set(info);
}
export function updateUserInfo(id, info) {
  return db.collection("users").doc(id).update(info);
}
export function getUser(id) {
  return db.collection("users").doc(id).get();
}
export function addListener(path, onSnapshot) {
  return db.doc(path).onSnapshot(onSnapshot);
}
export function startDrawing() {
  return db.collection("commands").doc("commandCenter").update({
    state: "started"
  });
}
export async function resetDrawing() {
  await db.collection("history").doc(new Date().getFullYear().toString()).delete();
  await db.collection("commands").doc("commandCenter").update({
    state: "not started"
  });
  let users = await db.collection("users").get();
  let promises = [];
  users.docs.forEach(doc => {
    promises.push(doc.ref.update({
      person: "",
      personDesire: "",
      personDislike: ""
    }));
  });
  await Promise.all(promises);
}
